INSERT INTO blog_post(title, text, author, publish_date, image)
VALUES
('История Amazon: книготорговец, который стал миллиардером', '27 июля 2017 года Джефф Безос, основатель Amazon, занял лидирующую позицию в рейтинге самых богатых людей мира по версии Bloomberg и Forbes. Его состояние оценили в $90,6 млрд. Когда-то компания Джеффа была небольшим онлайн-магазином по продаже книг, а теперь стала огромной корпорацией с годовой выручкой более $33 млрд и космическими проектами.', 1, '2024-08-01', 'images/1.png'),
('Главная помеха стать лучшей версией себя', 'Каждый, кто ищет способы повысить продуктивность и эффективность, ориентируется на способы разгона мозга и ЦНС. Фактически – пытается выжать больше энергии внутри дня, но оставаясь в рамках сохранения общего здоровья организма. Как ни странно, но такой подход далеко не всегда приносит плоды. Ведь иногда, чтобы двинуться с места, нужно отладить топливную систему.', 2, '2024-08-02', 'images/2.png');
